import { Injectable } from '@angular/core';
import {Observable} from "rxjs";
import {PlayerProfileRm} from "./player-profile.rm";
import {HttpClient} from "@angular/common/http";
import {Config, ConfigService} from "../config/config.service";


@Injectable({
  providedIn: 'root'
})
export class PlayerProfilesService {
  config: Config | undefined;

  constructor(private http: HttpClient, private configService: ConfigService) {
    configService.getConfig().subscribe(
      d => this.config = d
    );
  }

  getPlayerProfile(id: number): Observable<PlayerProfileRm[]>{
    if (this.config == undefined){
      this.configService.getConfig();
    }
    const path = `${this.config.apiRootUrl}${this.config.playerProfilesEndpoint}`;
    return this.http.get<PlayerProfileRm[]>(path);
  }
}
