import { Component } from '@angular/core';

@Component({
  selector: 'app-counter-component',
  templateUrl: './counter.component.html'
})
export class CounterComponent {
  public playerData = new PlayerData(10, 100, 5);

  public addRandomItem() {
    this.playerData.inventory.items.push(new Item(`Shield{Math.random()}`, "Description"))
  }
}

class Inventory {
  public readonly items: Item[] = [];

  public get empty(): boolean {
    return this.items.length == 0;
  }

  public get itemsCount(): number {
    return this.items.length;
  }
}

class Item {
  private static lastId = 0;
  public static readonly NONE_ITEM = "[none_item]";
  public static readonly NONE_ITEM_DESCRIPTION = "[none_item_description]";

  public id = -1;
  public name = Item.NONE_ITEM;
  public description = Item.NONE_ITEM_DESCRIPTION;

  constructor(name: string, description: string) {
    this.name = name;
    this.description = description;
    this.id = Item.lastId++;
  }
}

export class PlayerData {
  public level = 0;
  public hp = 0;
  public speed = 0;
  public inventory = new Inventory();

  constructor(level: number, hp: number, speed: number) {
    this.level = level;
    this.hp = hp;
    this.speed = speed;
    this.inventory = new Inventory();
  }
}
<h1>Counter</h1>

<p>This is a simple example of an Angular component.</p>

<p aria-live="polite">Current count: <strong>{{ playerData }}</strong></p>

<button class="btn btn-primary" (click)="addRandomItem()">Increment</button>
