import { Injectable } from '@angular/core';
import {Observable} from "rxjs";
import {PlayerProfileRm} from "./player-profile.rm";
import {HttpClient} from "@angular/common/http";
import {HashLocationStrategy, Location, LocationStrategy} from "@angular/common";


@Injectable({
  providedIn: 'root'
})
export class PlayerProfilesService {

  constructor(private http: HttpClient, private location: Location, private locationStrategy: HashLocationStrategy) { }

  getPlayerProfile(id: number): Observable<PlayerProfileRm[]>{
    const path = this.locationStrategy.prepareExternalUrl("/playerprofiles");
    return this.http.get<PlayerProfileRm[]>(path)
  }
}
